import React, { useState } from 'react';

const initialUser = {
    name:"",
    email: "",
    password: "",
    tos: false
}



export default function Form(props){

    const [userValue, setUserValue] = useState(initialUser);

    const changeHandler = (e) => {
        setUserValue({...userValue, [e.target.name]: e.target.value})
    }

    const handleSubmit = (evt) => {
        evt.preventDefault();
        props.setNewUser([...props.newUser,userValue])
    }

return(
    <form onSubmit={handleSubmit}>
        <label>
        Name:    
            <input
             name="name"
             type="text"
             placeholder="Name Here"
             value={userValue.name}
             onChange={changeHandler}
            />
        </label>
        <label>
        Email:        
            <input
            name="email"
            type="email"
            placeholder="Email Here"
            value={userValue.email}
            onChange={changeHandler}  
            />  
        </label>
        <label>
            <input
            name="password"
            type="password"
            placeholder="Email Here"
            value={userValue.password}
            onChange={changeHandler}  
            />
        </label>
        <label>
            <input
            name="tos"
            type="checkbox"
            value={userValue.tos}
            onChange={changeHandler}  
            />
        </label>
    </form>
)


}
